(async function a() {
  const fs = require('fs')
  const ejs = require('ejs')
  const arr = fs.readdirSync('./utils')
    .filter(x => x.endsWith('.js'))
    .map(e => e.substring(0, e.length - 3))
  fs.writeFileSync('./index.js', ejs.render(
`<% arr.forEach(function(e) { %>
const <%- e %> = require('./utils/<%- e %>')<% }) %>
module.exports = {
  <%- arr %>
}`, { arr }))
})()
