#!/usr/bin/env node
"use strict";
const { exec } = require('child_process');
function run() {
    console.log('Starting update internal dependency..');
    const yarnPath = (!['staging', 'production'].includes(process.env.ENV || '')
        ? '%NVM_HOME%/v%npm_package_engines_node%'
        : '$NVM_DIR/versions/node/v$npm_package_engines_node/lib')
        + '/node_modules/yarn/bin/yarn';
    exec(`${yarnPath} upgrade -S @ggj`, (err) => {
        if (err) {
            console.log('Upgrade internal dependency @ggj/* failed!');
            return;
        }
        console.log(`Upgraded all internal dependency @ggj/*.`);
    });
}
run();
